import { useState } from "react";
import React from "react";
import StackAnimation from "./stack-animation.jsx";

export default function Stack() {
    const [showAnimation, setShowAnimation] = useState(false); // State to control animation display

    function handleAnimation() {
        setShowAnimation(true); // Trigger animation and hide button/h1
    }

    return (
        <>
            {!showAnimation && ( // Only show h1 and button when the animation is not running
                <>
                    <h1>Mein Stack</h1>
                    <button onClick={handleAnimation}>Animate</button>
                </>
            )}
            {showAnimation && <StackAnimation />} {/* Show animation when button is clicked */}
        </>
    );
}
