import React, { useState } from 'react';
import './stack.css'; // Assuming your CSS for animations is in this file

const StackComponent = () => {
  const [isAnimating, setIsAnimating] = useState(false);

  // Function to handle the animation start
  const startAnimation = () => {
    setIsAnimating(true); // Set state to true when animation starts
    // Simulate the duration of the animation (example: 3 seconds)
    setTimeout(() => {
      setIsAnimating(false); // Set state to false when animation ends
    }, 3000);
  };

  return (
    <div>
      {/* Conditionally render the button and h1 based on animation state */}
      {!isAnimating && (
        <>
          <h1>This is a heading</h1>
          <button onClick={startAnimation}>Start Animation</button>
        </>
      )}

      {/* Animation container */}
      <div className={isAnimating ? 'animation-class' : ''}>
        {/* Your animation content goes here */}
        {isAnimating && <p>Animation is running...</p>}
      </div>
    </div>
  );
};

export default StackComponent;
