import { useState, useEffect } from "react";
import React from "react";
import StackAnimation from "./stack-animation.jsx";

export default function Stack() {
    const [showAnimation, setShowAnimation] = useState(false);

    function handleAnimation() {
        setShowAnimation(true); // Show animation
    }

    // Function to stop animation and hide StackAnimation component
    function stopAnimation() {
        setShowAnimation(false); // Hide animation on click anywhere
    }

    // Attach a click event listener to the entire document when animation is running
    useEffect(() => {
        if (showAnimation) {
            document.addEventListener('click', stopAnimation);
        }

        return () => {
            document.removeEventListener('click', stopAnimation); // Clean up event listener
        };
    }, [showAnimation]);

    return (
        <>
            {!showAnimation && (
                <>
                    <h1>Mein Stack</h1>
                    <button onClick={handleAnimation}>Animate</button>
                </>
            )}
            {showAnimation && <StackAnimation />} {/* Show animation when button is clicked */}
        </>
    );
}
