import React, { useState } from 'react';
import StackAnimation from './stack-animation.jsx'; // Import your animation component
import styles from './stack.module.css'; // Use lowercase for the CSS import

export default function Stack() {
    // State to track active tab
    const [activeTab, setActiveTab] = useState('Specs');

    // Handle tab click
    const handleTabClick = (e, tabName) => {
        e.stopPropagation(); // Prevent click from propagating to parent elements
        console.log(`Tab clicked: ${tabName}`); // Log to verify click
        setActiveTab(tabName);
    };

    return (
        <div className={styles.stackContainer} onClick={(e) => e.stopPropagation()}>
            {/* Tabs */}
            <div className={styles.tabs}>
                <button
                    onClick={(e) => handleTabClick(e, 'Specs')}
                    className={`${styles.tab} ${activeTab === 'Specs' ? styles.active : ''}`}
                >
                    Specs
                </button>
                <button
                    onClick={(e) => handleTabClick(e, 'Animation')}
                    className={`${styles.tab} ${activeTab === 'Animation' ? styles.active : ''}`}
                >
                    Animation
                </button>
            </div>

            {/* Tab Content */}
            <div className={styles.tabContent}>
                {activeTab === 'Specs' && (
                    <div className={styles.specs}>
                        <h2>Technical Specifications</h2>
                        <ul>
                            <li>Programming Languages: Python, C#, Java, C, C++</li>
                            <li>Frameworks: .NET Core, .NET Framework, MAUI, Xamarin, IoTHub, SignalR</li>
                            <li>Database: MySQL, MariaDB, MongoDB</li>
                            <li>Others: Git, Jira, Confluence, Microsoft Azure, Docker, Pandas, Tableau</li>
                        </ul>
                    </div>
                )}
                {activeTab === 'Animation' && (
                    <div className={styles.animation}>
                        <StackAnimation />
                    </div>
                )}
            </div>
        </div>
    );
}
