import { useState, useEffect } from "react";
import React from "react";
import StackAnimation from "./stack-animation.jsx";
import styles from './stack.module.css';

export default function Stack() {
    // State to track active tab
    const [activeTab, setActiveTab] = useState('Specs');

    // Function to handle tab switching
    const handleTabClick = (tabName) => {
        setActiveTab(tabName);
    };

    return (
        <div className={styles.stackContainer}>
            <div className={styles.tabs}>
                <div
                    className={`${styles.tab} ${activeTab === 'Specs' ? styles.active : ''}`}
                    onClick={() => handleTabClick('Specs')}
                >
                    Specs
                </div>
                <div
                    className={`${styles.tab} ${activeTab === 'Animation' ? styles.active : ''}`}
                    onClick={() => handleTabClick('Animation')}
                >
                    Animation
                </div>
            </div>
            <div className={styles.tabContent}>
                {activeTab === 'Specs' && (
                    <div className={styles.specs}>
                        <h2>Technical Specifications</h2>
                        <ul>
                            <li>Programming Languages: Python, C#, Java, C, C++</li>
                            <li>Frameworks: .NET Core, .NET Framework, MAUI, Xamarin, IoTHub, SignalR</li>
                            <li>Database: MySQL, MariaDB, MongoDB</li>
                            <li>Others: Git, Jira, Confluence, Microsoft Azure, Docker, Pandas, Tableau</li>
                        </ul>
                    </div>
                )}
                {activeTab === 'Animation' && (
                    <div className={styles.animation}>
                        <StackAnimation />
                    </div>
                )}
            </div>
        </div>
    );
}
