import { useState } from "react";
import React from "react";
import StackAnimation from "./stack-animation.jsx"; // Assuming this is correctly imported
import styles from './stack.module.css';

export default function Stack() {
    // Track active tab state
    const [activeTab, setActiveTab] = useState('Specs');

    const handleTabClick = (tabName) => {
        console.log(`Tab clicked: ${tabName}`); // Log click events to confirm
        setActiveTab(tabName);
    };

    return (
        <div style={{ padding: "20px" }}>
            {/* Tabs */}
            <div style={{ display: "flex", justifyContent: "space-around", cursor: "pointer" }}>
                <div
                    onClick={() => handleTabClick('Specs')}
                    style={{
                        padding: "10px",
                        borderBottom: activeTab === 'Specs' ? "2px solid blue" : "2px solid transparent"
                    }}
                >
                    Specs
                </div>
                <div
                    onClick={() => handleTabClick('Animation')}
                    style={{
                        padding: "10px",
                        borderBottom: activeTab === 'Animation' ? "2px solid blue" : "2px solid transparent"
                    }}
                >
                    Animation
                </div>
            </div>

            {/* Tab Content */}
            <div style={{ marginTop: "20px" }}>
                {activeTab === 'Specs' && (
                    <div>
                        <h2>Technical Specifications</h2>
                        <ul>
                            <li>Programming Languages: Python, C#, Java, C, C++</li>
                            <li>Frameworks: .NET Core, .NET Framework, MAUI, Xamarin, IoTHub, SignalR</li>
                            <li>Database: MySQL, MariaDB, MongoDB</li>
                            <li>Others: Git, Jira, Confluence, Microsoft Azure, Docker, Pandas, Tableau</li>
                        </ul>
                    </div>
                )}
                {activeTab === 'Animation' && (
                    <div>
                        <StackAnimation />
                    </div>
                )}
            </div>
        </div>
    );
}
